mapper.select:

     select * from `${table_name}` where `storeId`=#{storeId} 
     and `state`=#{state}  
     and (`state`=9 or `state`=10 or `state`=11 or`state`=12)  
       and #{startDate} <= `time` and `time` <= #{endDate} order by `time` desc limit #{start},#{count}


      select count(*) from `${table_name}` where `storeId`=#{storeId} 
     and `state`=#{state}  
     and `state`=10 or `state`=11 or`state`=12   
     and #{startDate} <= `time` and `time` <= #{endDate} order by `time` desc


      select * from `${table_name}` where `merchantId`=#{merchantId}        
       and `state`=#{state}      
       and (`state`= 1 or `state`= 4 or `state`= 5)      
       and `storeId`=#{storeId}        
       order by `time` desc limit #{start},#{count}
