mapper.select:

      select * from `marketing_full_reduces`  where 1=1
      
      and `merchantId`=#{merchantId}
      
      and state != 3
      order by beginDate
      limit #{start},#{count}


      select count(*) from `marketing_full_reduces`  where 1=1
      
      and `merchantId`=#{merchantId}
      
       and state != 3
