mapper.select:

      select * from `orderform_refund_order` 
       where 1=1 and `state`!=800
      
      	and storeId=#{storeId}
      
      
      	and merchantId=#{merchantId}
      
      
      	and state=#{state}
      
      
      	and refundNumber=#{refundNumber}
      
      order by time desc
      limit #{start},#{count}


      select count(*) from `orderform_refund_order`
       where 1=1 and `state`!=800
      
      	and storeId=#{storeId}
      
      
      	and merchantId=#{merchantId}
      
      
      	and state=#{state}
      
      
      	and refundNumber=#{refundNumber}
