mapper.select:

      select * from `organization_department` where 1=1
      
      	and `displayName` like concat('%',#{displayName},'%') 
      
      limit #{start},#{count}

select * from `organization_department` where 1=1 and bsid like CONCAT(#{bsid},'%')
and `displayName` like concat('%',#{displayName},'%')
order by bsid limit #{start},#{count}
