mapper.select:

      select `id`,`title`,`time`,`state`,`classify` from `${table_name}` where `receiver`=#{receiver} and `state` <> 3  
     	
      	and classify=#{classify}
      
      order by field(state,1,2),time desc limit #{start},#{count}


      select `id`,`title`, `content`,`time`,`state`,`classify`,`url` from `${table_name}` where `receiver`=#{receiver} and `state` <> 3  
      
      	and classify=#{classify}
      
      order by field(state,1,2),time desc limit #{start},#{count}
